import { useState } from "react";
import Box from "@mui/material/Box";
import Typography  from "@mui/material/Typography";
import {
    Button,
    TextField,
    useMediaQuery,
    useTheme
} from "@mui/material";

import { EditOutlined } from "@mui/icons-material";
import { Formik } from "formik";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setLogin } from "state/index";
import Dropzone from "react-dropzone";
import FlexBetween from "components/FlexBetween";

const registerSchema = yup.object().shape({
    firstName: yup.string().required("required"),
    LastName: yup.string().required("required"),
    email: yup.string().email("invalid email").required("required"),
    password: yup.string().required("required"),
    location: yup.string().required("required"),
    occupation: yup.string().required("required"),
    picture: yup.string().required("required"),
});

const LoginSchema = yup.object().shape({
    email: yup.string().email("invalid email").required("required"),
    password: yup.string().required("required"),
});

const initialValueRegister = {
    firstName: "",
    LastName: "",
    email: "",
    password: "",
    location: "",
    occupation: "",
    picture: "",
}

const initialValueLogin = {
    email: "",
    password: "",
}

const Form = () => {

    const [pageType, setpageType] = useState("login");
    const {palette} = useTheme();
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const isNonMobile = useMediaQuery("(min-width:600px)");
    const isLogin = pageType === "login";
    const isRegister = pageType === "register";

    const register = async (values, onSubmitProps) =>{
        //Allows us to send form info with image
        const formData = new FormData();
        for(let value in values){
            formData.append(value, values[value]);
        }
        formData.append("picturePath", values.picture.name);

        const savedUserResponse = await fetch(
            "https://localhost:3001/auth/register",
            {
                method: "POST",
                body: formData,
            }
        );

        const savedUser = await savedUserResponse.json();
        onSubmitProps.resetForm();

        if(savedUser){
            setpageType("login");
        }
    };

    const login = async (values, onSubmitProps) => {
        const loggedInResponse = await fetch(
            "https://localhost:3001/auth/login",
            {
                method: "POST",
                headers: { "content-type": "application/json" },
                body: JSON.stringify(values),
            }
        );

        const loggedIn = await loggedInResponse.json();
        onSubmitProps.resetForm();
        if(loggedIn){
            dispatch(
                setLogin({
                    user: loggedIn.user,
                    token: loggedIn.token,
                })
            );
            navigate("/home");
        }
    }

    const handleFormSubmit = async (values, onSubmitProps) => {

        if(isLogin) await login(values,onSubmitProps);
        if(isRegister) await register(values, onSubmitProps); 

    };
        return (
            <Formik
            onSubmit={handleFormSubmit}
            initialValues={isLogin ? initialValueLogin : initialValueRegister} 
            validationSchema={isLogin ? LoginSchema : registerSchema}
            >

                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    setFieldValue,
                    resetForm,
                }) =>(
                    <Form onSubmit={handleFormSubmit}>
                        <Box
                            display="grid"
                            gap = "30px"
                            gridTemplateColumns = "repeat(4, minmax(0, 1fr))"
                            sx={{
                                "& > div" :{
                                    gridColumn : isNonMobile ? undefined : "span 4"},
                            }}
                        >
                            {isRegister &&  (
                                <>
                                <TextField
                                label="First name"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.firstName}
                                name = "FirstName"
                                error={Boolean(touched.firstName) && Boolean(errors.firstName)}
                                helperText={touched.firstName && errors.firstName}
                                sx ={{gridColumn: "span 2"}}
                                />

                                <TextField
                                label="Last name"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.LastName}
                                name = "lastName"
                                error={Boolean(touched.LastName) && Boolean(errors.LastName)}
                                helperText={touched.LastName && errors.LastName}
                                sx ={{gridColumn: "span 2"}}
                                />

                                <TextField
                                label="location"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.location}
                                name = "location"
                                error={Boolean(touched.location) && Boolean(errors.location)}
                                helperText={touched.location && errors.location}
                                sx ={{gridColumn: "span 2"}}
                                />

                                <TextField
                                label="occupation"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.occupation}
                                name = "occupation"
                                error={Boolean(touched.occupation) && Boolean(errors.occupation)}
                                helperText={touched.occupation && errors.occupation}
                                sx ={{gridColumn: "span 2"}}
                                />

                                <Box gridColumn="span 4"
                                    border ={`1px solid ${palette.neutral.medium}`}
                                    borderRadius = "5px"
                                    p="1rem"
                                    >
                                        <Dropzone
                                        acceptedFile = ".jpg, .jpeg, .png"
                                        multiple = {false}
                                            onDrop = {(acceptedFiles) => 
                                            setFieldValue("picture", acceptedFiles[0])
                                        }
                                        >
                                            
                                            {({ getRootProps, getInputProps}) =>(
                                                <Box
                                                {...getRootProps()}
                                                border ={`2px solid ${palette.primay.main}`}
                                                p="1rem"
                                                sx={{ "&:hover" : {cursor : "pointer"}}}
                                                >

                                                    <input {...getInputProps()} />
                                                    {!values.picture ? (
                                                        <p>Add Picture Here</p> 
                                                    ) : (<FlexBetween>
                                                        <Typography>
                                                            {values.picture.name}
                                                            <EditOutlined />
                                                        </Typography>
                                                    </FlexBetween>)}

                                                </Box>
                                            )}

                                        </Dropzone>
                                
                                </Box>

                                </>
                            )}

                            <TextField
                                label="Email"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.email}
                                name = "email"
                                error={Boolean(touched.email) && Boolean(errors.email)}
                                helperText={touched.email && errors.email}
                                sx ={{gridColumn: "span 2"}}
                                /> 

                            <TextField
                                label="Password"
                                type="password"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.password}
                                name = "password"
                                error={Boolean(touched.password) && Boolean(errors.password)}
                                helperText={touched.password && errors.password}
                                sx ={{gridColumn: "span 2"}}
                                />      
                            

                        </Box>

                        {/* Button */}
                        <Box>
                            <Button
                            fullWidth
                            type="submit"
                            sx ={{
                                m:"2rem 0",
                                p: "1rem",
                                backgroundColor: palette.primary.main,
                                color: palette.background.alt,
                                "&:hover": {color: palette.primary.main},
                                                            
                            }}
                            >
                                {isLogin ? "LOGIN" : "REGISTER"}
                            </Button>
                            <Typography
                                onClick={()=> {
                                setpageType(isLogin ? "register" : "login");
                                resetForm();
                            }}
                            sx={{
                                textDecoration:"underline",
                                color: palette.primary.main,
                                "&:hover" :{
                                    cursor: "pointer",
                                    color: palette.primary.light,
                                },
                            }}
                            >
                                {isLogin ? "Don't have an account? Sign Up here." 
                                : "Already Have an account? Login here."}
                            </Typography>
                        </Box>

                    </Form>
                )}

            </Formik>
        );
};

export default Form;
